import React, { useState } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './User.css';
const User = (props) => {
    const { name, email, img, yearlyIncome, address } = props.user;
    const { street, suite, city, zipcode } = address;
    const [friend,setFriend] = useState(false);
    let button= <button className="btn btn-primary" onClick={()=>{props.addSalaryHandler(props.user);setFriend(true)}}>Add Friend</button>
    if (friend) {
        button= <button className="btn btn-danger" onClick={()=>{props.removeSalaryHandler(props.user);setFriend(false)}}>Remove friend</button>
    }
    return (
            <div className="card float-left text-center mb-3 mx-2" >
                <img className="card-img-top" style={{ height: '350px', maxWidth: '18rem' }} src={img} alt="" />
                <div className="card-body">
                    <h4 className="card-title">{name}</h4>
                    {button}
                </div>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item">email: {email}</li>
                    <li className="list-group-item">Yearly Income : ${yearlyIncome}</li>
                </ul>
                <p className="card-text"><b> Address:</b> &nbsp;
                    {street},
                    <br />
                    {suite},
                    <br />
                    {city},
                    <br />
                    {zipcode}
                </p>
            </div>
    );
};

export default User;
