import React, { useState } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const Salary = (props) => {
    const [friend,setFriend] = useState(true); 
    if (friend) {
        const income = props.addIncome;
        const totalIncome = income.reduce((totalIncome,user) => totalIncome + Number(user.yearlyIncome),0);
        const allName = income.reduce((allName,user) => allName +" "+ user.name +",","");
    }
    
    
    //remove friend income
    

    
    return (
        <div>
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">Total Friends: {income.length}</div>
                <div className="card-body">
                    <h5 className="card-title">Total Yearly Income: {totalIncome}</h5>
                    <p className="card-text">Name: {allName}</p>
                </div>
            </div>
        </div>
    );
};

export default Salary;
