import React, { useState } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import fakeData from '../../fakeData';
import User from '../User/User';
import Salary from '../Salary/Salary';

const AppBody = () => {
    const [users,setUsers] = useState(fakeData);
    const [income, setIncome] = useState([]);
    const addSalaryHandler = (user) => {
        const newIncome = [...income, user];
        setIncome(newIncome); 
    };
    const removeSalaryHandler = (user)=>{
        const newIncome = [...income, user];
        setIncome(newIncome); 
    }
    return (
        <div className="row mx-auto">
        <div className="col-9">
        {
        users.map(user => <User user={user} addSalaryHandler={addSalaryHandler} removeSalaryHandler={removeSalaryHandler}></User>)
        }
        </div>
        <div className="col-3">
            <Salary income={income}></Salary>
        </div>
        </div>
    );
};

export default AppBody;
