import React, { useState } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';

const Salary = (props) => {
    let income = props.addIncome;
    let totalIncome = income.reduce((totalIncome,user) => totalIncome + Number(user.yearlyIncome),0);
    let allName = income.reduce((allName,user) => allName +" "+ user.name +",","");
    let [friend,setFriend] = useState(true); 
    if (friend) {
        income = props.removeIncome;
        totalIncome = income.reduce((totalIncome,user) => totalIncome - Number(user.yearlyIncome),0);
        allName = income.reduce((allName,user) => allName -" "+ user.name +",","");
        setFriend(false);
    }
    
    
    //remove friend income
    

    
    return (
        <div>
            <div className="card text-white bg-dark mb-3">
                <div className="card-header">Total Friends: {income.length}</div>
                <div className="card-body">
                    <h5 className="card-title">Total Yearly Income: {totalIncome}</h5>
                    <p className="card-text">Name: {allName}</p>
                </div>
            </div>
        </div>
    );
};

export default Salary;
