import React, { useState } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './User.css';
import Salary from '../Salary/Salary';
const User = (props) => {
    const { name, email, img, yearlyIncome, address } = props.user;
    const { street, suite, city, zipcode } = address;
    return (
        <div className="container-fluid">
            <div className="col-10">
            <div className="card float-left text-center mb-3 mx-auto" style={{ width: '20rem' }}>
                <img className="card-img-top" style={{ height: '350px' }} src={img} alt="" />
                <div className="card-body">
                    <h4 className="card-title">{name}</h4>
                    <button className="btn btn-primary">Add Friend</button>
                </div>
                <ul className="list-group list-group-flush">
                    <li className="list-group-item">email: {email}</li>
                    <li className="list-group-item">Yearly Income : ${yearlyIncome}</li>
                </ul>
                <p className="card-text"><b> Address:</b> &nbsp;
                    {street},
                    <br />
                    {suite},
                    <br />
                    {city},
                    <br />
                    {zipcode}
                </p>
            </div>
            </div>
            <div className="col-2 float-none">
               <Salary></Salary>
            </div>
        </div>
    );
};

export default User;
