import React, { useState } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import fakeData from '../../fakeData';
import User from '../User/User';
import Salary from '../Salary/Salary';

const AppBody = () => {
    const [users,setUsers] = useState(fakeData);
    const [yearlyIncome, setYearlyIncome] = useState([]);
    const addSalaryHandler = (user) => {
        const newYearlyIncome = [...yearlyIncome, user];
        setYearlyIncome(newYearlyIncome);
    };
    return (
        <div className="row">
        <div className="col-10">
        {
        users.map(user => <User user={user} addSalaryHandler={addSalaryHandler}></User>)
        }
        </div>
        <div className="col-2">
            <Salary yearlyIncome={yearlyIncome}></Salary>)
        </div>
        </div>
    );
};

export default AppBody;
